    /// <summary>
    /// 单笔代付(商户->平台)
    /// </summary>
    public class OnePayment
    {
        public string version;//版本
        public string merchant_code;//商户号
        public string requestno;//请求流水
        public int money;//金额(单位分)
        public string money_type;//币种（CNY-人民币）
        public int product_type;//代付类型(11001余额代付，11002额度代付)
        public string acct_name;//收款人姓名
        public string acct_no;//收款人账号
        public string acct_type;//账号类型（21001借记卡 21002贷记卡）
        public string phone;//银行预留手机号
        public string bank_settle_no;//开户行行号（对公必传，开户行所在支行的行号，对私不传）
        public string bank_branch_name;//开户行支行名称(可无)
        public string remark;//摘要（简单的附言）
        public string province;//开户省（可无）
        public string city;//开户市（可无）
        public string sign;//签名
    }
